function Tutorial () {
    story.printCharacterText("Alright now I will show you how Goal Lock works", "Ego")
    Ego.setPosition(20, 38)
    Ego_PlaceHolder = statusbars.create(40, 10, StatusBarKind.Placeholder)
    Ego_PlaceHolder.setPosition(85, 40)
    Ego_PlaceHolder.setColor(10, 12, 11)
    Ego_PlaceHolder.setBarBorder(1, 15)
    Ego_PlaceHolder.setLabel("Ego", 15)
    Ego_PlaceHolder.max = 10000000
    story.printCharacterText("This is your ego bar it shows how much ego you have ", "Ego")
    story.printCharacterText("You earn ego for every goal you win and game you play and lose some for everyone you lose", "Ego")
    Ego_PlaceHolder.value = 10000000
    story.printCharacterText("This bar boosts your fame and your strength during games", "Ego")
    Ego_PlaceHolder.value = 50000
    Ego_PlaceHolder.setLabel("Ego = 50000", 15)
    story.printCharacterText("If your ego reaches zero for some characters you won't be able to use there moves", "Ego")
    Ego_PlaceHolder.value = 0
    Ego_PlaceHolder.setLabel("Ego", 15)
    story.printCharacterText("Next is your fame I won't show you the bar because we're running out of time but you'll see it ", "Ego")
    info.startCountdown(20)
    story.printCharacterText("If your fame reaches zero your out of blue lock", "Ego")
    Ego.setPosition(85, 40)
    sprites.destroy(Ego_PlaceHolder)
    story.printCharacterText("That's all for now. Now its time for your entry match good luck", "Ego")
    sprites.destroy(Ego)
    info.stopCountdown()
    Fame_bar = statusbars.create(40, 10, StatusBarKind.Fame)
    Fame_bar.setColor(1, 13, 5)
    Fame_bar.setLabel("Fame")
    Fame_bar.setBarBorder(1, 5)
    Fame_bar.value = 10
    Fame_bar.setFlag(SpriteFlag.RelativeToCamera, true)
    Ego_Bar = statusbars.create(40, 10, StatusBarKind.Ego)
    Ego_Bar.setColor(10, 12, 11)
    Ego_Bar.setBarBorder(1, 15)
    Ego_Bar.setLabel("Ego")
    Ego_Bar.setFlag(SpriteFlag.RelativeToCamera, true)
    Fame_bar.setPosition(20, 8)
    Ego_Bar.setPosition(124, 8)
    Entry_match()
}
